  "lwt" {>= "2.7.0"}
  "lwt_log"
  "react"
  "zed" {>= "1.2"}
  "camomile" {>= "0.8.6"}
  "lwt_react"
  "jbuilder" {build & >= "1.0+beta9"}
]
available: ocaml-version >= "4.02.3"
build: [
